pub enum BrushAction<V> {

    /// Draw new/changed vertix data.
    Draw(Vec<V>),

    /// Re-draw last frame's vertices unmodified.
    ReDraw,
}

pub enum BrushError {

    /// Texture is too small to cache queued glyphs
    ///
    /// A larger suggested size is included.
    TextureTooSmall {
        suggested: (u32, u32),
    },
}
