pub enum ConnectError {

    /// The library was compiled with the `dlopen` feature, and the `libwayland-client.so`
    /// library could not be found at runtime
    NoWaylandLib,

    /// The `XDG_RUNTIME_DIR` variable is not set while it should be
    XdgRuntimeDirNotSet,

    /// Any needed library was found, but the listening socket of the server could not be
    /// found.
    ///
    /// Most of the time, this means that the program was not started from a wayland session.
    NoCompositorListening,

    /// The provided socket name is invalid
    InvalidName,

    /// The FD provided in `WAYLAND_SOCKET` was invalid
    InvalidFd,
}

pub enum GlobalError {

    /// The requested global was missing
    Missing,

    /// The global advertised by the server has a lower version number
    /// than the one requested
    VersionTooLow(u32),
}

pub enum GlobalEvent {

    /// A new global was created
    New {
        /// Id of the new global
        id: u32,
        /// Interface of the new global
        interface: String,
        /// Maximum supported version of the new global
        version: u32,
    },

    /// A global was removed
    Removed {
        /// Id of the removed global
        id: u32,
        /// Interface of the removed global
        interface: String,
    },
}

pub enum Event {

    #[doc =
      "fatal error event\n\nThe error event is sent out when a fatal (non-recoverable)\nerror has occurred.  The object_id argument is the object\nwhere the error occurred, most often in response to a request\nto that object.  The code identifies the error and is defined\nby the object interface.  As such, each interface defines its\nown set of error codes.  The message is a brief description\nof the error, for (debugging) convenience."]
    Error {
        object_id: AnonymousObject,
        code: u32,
        message: String,
    },

    #[doc =
      "acknowledge object ID deletion\n\nThis event is used internally by the object ID management\nlogic.  When a client deletes an object, the server will send\nthis event to acknowledge that it has seen the delete request.\nWhen the client receives this event, it will know that it can\nsafely reuse the object ID."]
    DeleteId {
        id: u32,
    },

    #[doc(hidden)]
    __nonexhaustive,
}

pub enum Request {

    #[doc =
      "bind an object to the display\n\nBinds a new, client-created object to the server using the\nspecified name as the identifier."]
    Bind {
        name: u32,
        id: (String, u32, AnonymousObject),
    },

    #[doc(hidden)]
    __nonexhaustive,
}

pub enum Event {

    #[doc =
      "announce global object\n\nNotify the client of global objects.\n\nThe event notifies the client that a global object with\nthe given name is now available, and it implements the\ngiven version of the given interface."]
    Global {
        name: u32,
        interface: String,
        version: u32,
    },

    #[doc =
      "announce removal of global object\n\nNotify the client of removed global objects.\n\nThis event notifies the client that the global identified\nby name is no longer available.  If the client bound to\nthe global using the bind request, the client should now\ndestroy that object.\n\nThe object remains valid and requests to the object will be\nignored until the client destroys it, to avoid races between\nthe global going away and a client sending a request to it."]
    GlobalRemove {
        name: u32,
    },

    #[doc(hidden)]
    __nonexhaustive,
}

