pub enum Format {

    /// Premultiplied R8G8B8A8, little-endian.
    Rgba32,

    /// R8G8B8, little-endian.
    Rgb24,

    /// A8.
    A8,
}

pub enum RasterizationOptions {

    /// "Black-and-white" rendering. Each pixel is either entirely on or off.
    Bilevel,

    /// Grayscale antialiasing. Only one channel is used.
    GrayscaleAa,

    /// Subpixel RGB antialiasing, for LCD screens.
    SubpixelAa,
}

pub enum FontLoadingError {

    /// The data was of a format the loader didn't recognize.
    UnknownFormat,

    /// Attempted to load an invalid index in a TrueType or OpenType font collection.
    ///
    /// For example, if a `.ttc` file has 2 fonts in it, and you ask for the 5th one, you'll get
    /// this error.
    NoSuchFontInCollection,

    /// Attempted to load a malformed or corrupted font.
    Parse,

    /// Attempted to load a font from the filesystem, but there is no filesystem (e.g. in
    /// WebAssembly).
    NoFilesystem,

    /// A disk or similar I/O error occurred while attempting to load the font.
    Io(io::Error),
}

pub enum GlyphLoadingError {

    /// The font didn't contain a glyph with that ID.
    NoSuchGlyph,

    /// A platform function returned an error.
    PlatformError,
}

pub enum SelectionError {

    /// No font matching the given query was found.
    NotFound,

    /// The source was inaccessible because of an I/O or similar error.
    CannotAccessSource,
}

pub enum FamilyName {

    /// A specific font family, specified by name: e.g. "Arial", "times".
    Title(String),

    /// Serif fonts represent the formal text style for a script.
    Serif,

    /// Glyphs in sans-serif fonts, as the term is used in CSS, are generally low contrast
    /// (vertical and horizontal stems have the close to the same thickness) and have stroke
    /// endings that are plain — without any flaring, cross stroke, or other ornamentation.
    SansSerif,

    /// The sole criterion of a monospace font is that all glyphs have the same fixed width.
    Monospace,

    /// Glyphs in cursive fonts generally use a more informal script style, and the result looks
    /// more like handwritten pen or brush writing than printed letterwork.
    Cursive,

    /// Fantasy fonts are primarily decorative or expressive fonts that contain decorative or
    /// expressive representations of characters.
    Fantasy,
}

pub enum FileType {

    /// The font file represents a single font (`.ttf`, `.otf`, `.woff`, etc.)
    Single,

    /// The font file represents a collection of fonts (`.ttc`, `.otc`, etc.)
    Collection(u32),
}

pub enum Handle {

    /// A font on disk referenced by a path.
    Path {
        /// The path to the font.
        path: PathBuf,
        /// The index of the font, if the path refers to a collection.
        ///
        /// If the path refers to a single font, this value will be 0.
        font_index: u32,
    },

    /// A font in memory.
    Memory {
        /// The raw TrueType/OpenType/etc. data that makes up this font.
        bytes: Arc<Vec<u8>>,
        /// The index of the font, if the memory consists of a collection.
        ///
        /// If the memory consists of a single font, this value will be 0.
        font_index: u32,
    },
}

pub enum HintingOptions {

    /// No hinting is performed unless absolutely necessary to assemble the glyph.
    ///
    /// This corresponds to what macOS and FreeType in its "no hinting" mode do.
    None,

    /// Hinting is performed only in the vertical direction. The specified point size is used for
    /// grid fitting.
    ///
    /// This corresponds to what DirectWrite and FreeType in its light hinting mode do.
    Vertical(f32),

    /// Hinting is performed only in the vertical direction, and further tweaks are applied to make
    /// subpixel antialiasing look better. The specified point size is used for grid fitting.
    ///
    /// This matches DirectWrite, GDI in its ClearType mode, and FreeType in its LCD hinting mode.
    VerticalSubpixel(f32),

    /// Hinting is performed in both horizontal and vertical directions. The specified point size
    /// is used for grid fitting.
    ///
    /// This corresponds to what GDI in non-ClearType modes and FreeType in its normal hinting mode
    /// do.
    Full(f32),
}

pub enum Style {

    /// A face that is neither italic not obliqued.
    Normal,

    /// A form that is generally cursive in nature.
    Italic,

    /// A typically-sloped version of the regular face.
    Oblique,
}
